_.forEach(section.getSelectedRows(), function (row) {
    var artifact = row.getValue('ArtifactName');
    var moduleName = row.getValue('ModuleName');
    var artifactType = row.getValue('ArtifactType');
    row.update('ArtifactOutput', 'STARTING IMPORT', true);
    Http.post('/import/' + moduleName + '/' + artifactType + '/' + artifact).success(function (data) {
        row.update('ArtifactOutput', data.message || data, true);
    }).error(function (data) {
        row.update('ArtifactOutput', data, true);
    });
});

