_.forEach(section.getSelectedRows(), function (row) {
    var table = row.getValue('ArtifactName');
    if (row.getValue('ArtifactType') !== 'tables') {
        row.update('ArtifactOutput', 'ERROR: You can only create Tables', true);
        return;
    }
    row.update('ArtifactOutput', 'CREATING TABLE', true);
    Http.post('/ddl/' + table).success(function (data) {
        row.update('ArtifactOutput', data.message || data, true);
    }).error(function (data) {
        row.update('ArtifactOutput', data, true);
    });
});
